import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedCapacity, setSelectedCapacity] = useState(null);

  const hasCapacity = Boolean(product?.capacityAvailable?.length);

  /* ================= LOAD PRODUCT ================= */
  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      const found = items.find(p => p.id === productId);
      setProduct(found || null);

      if (found) {
        setSelectedColor(found.color || found.colorsAvailable?.[0] || null);
        setSelectedCapacity(
          found.capacity || found.capacityAvailable?.[0] || null
        );
      }

      setLoading(false);
    });
  }, [category, productId]);

  /* ================= PRICE ================= */
  const computedPrice = useMemo(() => {
    if (!product) return null;
    if (!hasCapacity) return product.priceDiscount;

    const baseIndex = product.capacityAvailable.indexOf(product.capacity);
    const currentIndex = product.capacityAvailable.indexOf(selectedCapacity);

    return product.priceDiscount + (currentIndex - baseIndex) * 100;
  }, [product, selectedCapacity, hasCapacity]);

  /* ================= IMAGE ================= */
  const computedImage = useMemo(() => {
    if (!product || !selectedColor || !product.images?.length) {
      return null;
    }

    const baseImage = product.images[0];
    const basePath = baseImage.split(`/${product.color}/`)[0];

    return `${basePath}/${selectedColor}/00.webp`;
  }, [product, selectedColor]);

  if (loading) return <div className="page">Loading...</div>;
  if (!product) return <div className="page">Product not found</div>;

  /* ================= CONFIG ================= */
  const configId = hasCapacity
    ? `${product.namespaceId}-${selectedColor}-${selectedCapacity}`
    : `${product.namespaceId}-${selectedColor}`;

  const isInCart = cart.some(item => item.configId === configId);
  const isFavorite = favorites.some(item => item.configId === configId);

  /* ================= PREPARED PRODUCT ================= */
  const preparedProduct = {
    ...product,

    // üî• –ö–õ–Æ–ß–ï–í–û–ï
    color: selectedColor,
    capacity: selectedCapacity,

    configId,
    priceDiscount: computedPrice,
    image: computedImage,
  };

  /* ================= TITLE ================= */
  const title = useMemo(() => {
    if (!product) return '';

    let base = product.name.replace(product.color, selectedColor || '');
    if (hasCapacity && selectedCapacity) {
      base = base.replace(product.capacity, selectedCapacity);
    }

    return base.trim();
  }, [product, selectedColor, selectedCapacity, hasCapacity]);

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {title}
      </h1>

      {/* ================= MAIN GRID ================= */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '360px 1fr',
          gap: 48,
          marginTop: 32,
          alignItems: 'start',
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: 24,
          }}
        >
          {computedImage && (
            <img
              src={`/${computedImage}`}
              alt={title}
              style={{
                maxWidth: '260px',
                maxHeight: '420px',
                objectFit: 'contain',
                pointerEvents: 'none',
                filter: `
                  drop-shadow(0 0 18px rgba(255,255,255,0.9))
                  drop-shadow(0 0 36px rgba(255,255,255,0.6))
                `,
              }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${computedPrice}
          </p>

          {/* COLORS */}
          {product.colorsAvailable && (
            <>
              <h3>Available colors</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.colorsAvailable.map(color => (
                  <button
                    key={color}
                    onClick={() => setSelectedColor(color)}
                    style={{
                      width: 22,
                      height: 22,
                      borderRadius: '50%',
                      border: 'none',
                      cursor: 'pointer',
                      background: color,
                      boxShadow:
                        color === selectedColor
                          ? '0 0 10px rgba(255,255,255,1)'
                          : '0 0 6px rgba(255,255,255,0.4)',
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {/* CAPACITY (ONLY IF EXISTS) */}
          {hasCapacity && (
            <>
              <h3 style={{ marginTop: 24 }}>Capacity</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.capacityAvailable.map(cap => (
                  <button
                    key={cap}
                    className="neon-btn"
                    onClick={() => setSelectedCapacity(cap)}
                    style={{
                      padding: '6px 12px',
                      opacity: cap === selectedCapacity ? 1 : 0.5,
                    }}
                  >
                    {cap}
                  </button>
                ))}
              </div>
            </>
          )}

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className={`neon-btn ${isInCart ? 'active' : ''}`}
              disabled={isInCart}
              onClick={() => addToCart(preparedProduct)}
            >
              {isInCart ? '‚úì Added' : 'Add to cart'}
            </button>

            <button
              className={`neon-btn ${isFavorite ? 'active' : ''}`}
              onClick={() => !isFavorite && addToFavorites(preparedProduct)}
            >
              {isFavorite ? '‚ô• Favorited' : '‚ô° Favorite'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
