import { NavLink } from 'react-router-dom';

function Header() {
  const linkStyle = ({ isActive }) => ({
    marginRight: '16px',
    textDecoration: 'none',
    fontWeight: isActive ? 'bold' : 'normal',
    color: isActive ? '#000' : '#555',
  });

  return (
    <header style={styles.header}>
      <h2 style={styles.logo}>âš¡ Phone Catalog</h2>

      <nav>
        <NavLink to="/" style={linkStyle}>
          Home
        </NavLink>

        <NavLink to="/catalog" style={linkStyle}>
          Catalog
        </NavLink>

        <NavLink to="/phones" style={linkStyle}>
          Phones
        </NavLink>

        <NavLink to="/tablets" style={linkStyle}>
          Tablets
        </NavLink>

        <NavLink to="/accessories" style={linkStyle}>
          Accessories
        </NavLink>

        <NavLink to="/favorites" style={linkStyle}>
          Favorites
        </NavLink>

        <NavLink to="/cart" style={linkStyle}>
          Cart
        </NavLink>
      </nav>
    </header>
  );
}

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '12px 24px',
    borderBottom: '1px solid #ddd',
  },
  logo: {
    margin: 0,
  },
};

export default Header;
