import { useSearchParams } from 'react-router-dom';
import { useMemo, useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import './Catalog.css';

const SORT_OPTIONS = {
  age: 'Newest',
  title: 'Alphabetically',
  price: 'Cheapest',
};

const PER_PAGE_OPTIONS = ['all', 8, 16];

function Catalog({
  products,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const [searchParams, setSearchParams] = useSearchParams();
  const [loading, setLoading] = useState(true);

  const sort = searchParams.get('sort') || 'age';
  const perPage = searchParams.get('perPage') || 'all';

  useEffect(() => {
    const t = setTimeout(() => setLoading(false), 300);
    return () => clearTimeout(t);
  }, []);

  const sortedProducts = useMemo(() => {
    const list = [...products];

    switch (sort) {
      case 'title':
        return list.sort((a, b) => a.name.localeCompare(b.name));
      case 'price':
        return list.sort((a, b) => a.priceDiscount - b.priceDiscount);
      default:
        return list.sort((a, b) => b.year - a.year);
    }
  }, [products, sort]);

  const visibleProducts = useMemo(() => {
    if (perPage === 'all') return sortedProducts;
    return sortedProducts.slice(0, Number(perPage));
  }, [sortedProducts, perPage]);

  if (loading) {
    return <div className="catalog-loading">Loadingâ€¦</div>;
  }

  return (
    <section className="catalog">
      <h1 className="catalog-title">Products</h1>

      <div className="catalog-controls">
        <label>
          Sort by:
          <select
            value={sort}
            onChange={(e) =>
              setSearchParams({ sort: e.target.value, perPage })
            }
          >
            {Object.entries(SORT_OPTIONS).map(([key, label]) => (
              <option key={key} value={key}>
                {label}
              </option>
            ))}
          </select>
        </label>

        <label>
          Items on page:
          <select
            value={perPage}
            onChange={(e) =>
              setSearchParams({ sort, perPage: e.target.value })
            }
          >
            {PER_PAGE_OPTIONS.map(opt => (
              <option key={opt} value={opt}>
                {opt}
              </option>
            ))}
          </select>
        </label>
      </div>

      <div className="catalog-grid">
        {visibleProducts.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            cart={cart}
            favorites={favorites}
            addToCart={addToCart}
            addToFavorites={addToFavorites}
          />
        ))}
      </div>
    </section>
  );
}

export default Catalog;
