import { useParams, Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    let loader;

    if (category === 'phones') loader = getPhones;
    if (category === 'tablets') loader = getTablets;
    if (category === 'accessories') loader = getAccessories;

    if (!loader) {
      setError(true);
      setLoading(false);
      return;
    }

    loader()
      .then(data => {
        const found = data.find(item => item.id === productId);

        if (!found) {
          setError(true);
        } else {
          setProduct(found);
        }
      })
      .catch(() => setError(true))
      .finally(() => setLoading(false));
  }, [category, productId]);

  if (loading) {
    return <h2>Loading...</h2>;
  }

  if (error || !product) {
    return <h2>Product was not found</h2>;
  }

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div style={{ padding: 20 }}>
      {/* üîπ BREADCRUMBS */}
      <nav style={styles.breadcrumbs}>
        <Link to="/">Home</Link>
        <span> / </span>
        <Link to={`/${category}`}>
          {category.charAt(0).toUpperCase() + category.slice(1)}
        </Link>
        <span> / </span>
        <span>{product.name}</span>
      </nav>

      {/* üîπ BACK BUTTON */}
      <button onClick={() => navigate(-1)} style={styles.back}>
        ‚Üê Back
      </button>

      <h1>{product.name}</h1>

      <img
        src={product.images?.[0]}
        alt={product.name}
        style={styles.image}
      />

      <p><strong>Price:</strong> ${product.priceDiscount}</p>

      <p>{product.description?.[0]?.text}</p>

      <div style={styles.actions}>
        <button
          onClick={() => addToCart(product)}
          disabled={isInCart}
        >
          {isInCart ? '‚úì Added to cart' : 'Add to cart'}
        </button>

        <button
          onClick={() => addToFavorites(product)}
          disabled={isFavorite}
        >
          {isFavorite ? '‚ô• In favorites' : '‚ô° Add to favorites'}
        </button>
      </div>
    </div>
  );
}

const styles = {
  breadcrumbs: {
    marginBottom: '12px',
    fontSize: '14px',
  },
  back: {
    marginBottom: '16px',
  },
  image: {
    width: '300px',
    display: 'block',
    marginBottom: '16px',
  },
  actions: {
    marginTop: '16px',
    display: 'flex',
    gap: '12px',
  },
};

export default ProductDetails;
