const addToCart = (product) => {
setCart(prev => {
if (prev.some(item => item.configId === product.configId)) {
return prev;
}
return [...prev, product];
});
};

const removeFromCart = (configId) => {
setCart(prev => prev.filter(item => item.configId !== configId));
};

const addToFavorites = (product) => {
setFavorites(prev => {
if (prev.some(item => item.configId === product.configId)) {
return prev;
}
return [...prev, product];
});
};

const removeFromFavorites = (configId) => {
setFavorites(prev => prev.filter(item => item.configId !== configId));
};
