import { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function Catalog({ category, addToCart, addToFavorites }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);

    let loader;

    if (category === 'phones') {
      loader = getPhones;
    }

    if (category === 'tablets') {
      loader = getTablets;
    }

    if (category === 'accessories') {
      loader = getAccessories;
    }

    loader().then(data => {
      setProducts(data);
      setLoading(false);
    });
  }, [category]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{category.toUpperCase()}</h1>

      <div style={styles.grid}>
        {products.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            addToCart={addToCart}
            addToFavorites={addToFavorites}
          />
        ))}
      </div>
    </div>
  );
}

const styles = {
  grid: {
    display: 'flex',
    gap: '16px',
    flexWrap: 'wrap',
  },
};

export default Catalog;
