function ProductCard({
  product,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  const handleFavorite = () => {
    if (!isFavorite) {
      addToFavorites(product);
    }
  };

  return (
    <div style={styles.card}>
      <img
        src={product.images?.[0]}
        alt={product.name}
        style={styles.image}
      />

      <h3 style={styles.title}>{product.name}</h3>

      <p style={styles.category}>{product.category}</p>

      <p style={styles.price}>
        ${product.priceDiscount}
      </p>

      <div style={styles.actions}>
        <button
          onClick={() => addToCart(product)}
          disabled={isInCart}
          style={{
            ...styles.neonButton,
            ...(isInCart ? styles.neonDisabled : {}),
          }}
        >
          {isInCart ? '✓ Added' : 'Add to cart'}
        </button>

        <button
          onClick={handleFavorite}
          style={{
            ...styles.favoriteBtn,
            ...(isFavorite ? styles.favoriteActive : {}),
          }}
        >
          {isFavorite ? '♥ Added' : '♡ Favorite'}
        </button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: '220px',
    padding: '14px',
    border: '1px solid rgba(179,107,255,0.4)',
    borderRadius: '10px',
    background: 'rgba(15,18,35,0.6)',
    boxShadow: '0 0 14px rgba(179,107,255,0.15)',
  },

  image: {
    width: '100%',
    height: '140px',
    objectFit: 'contain',
    marginBottom: '10px',
  },

  title: {
    fontSize: '14px',
    margin: '6px 0',
  },

  category: {
    fontSize: '11px',
    opacity: 0.6,
  },

  price: {
    fontWeight: '700',
    margin: '10px 0',
    textShadow: '0 0 6px rgba(179,107,255,0.7)',
  },

  actions: {
    display: 'flex',
    gap: '8px',
  },

  neonButton: {
    padding: '8px 12px',
    border: '1px solid #b36bff',
    borderRadius: '6px',
    cursor: 'pointer',
    color: '#e6e6f0',
    textShadow: '0 0 6px rgba(179,107,255,0.8)',
    boxShadow: '0 0 8px rgba(179,107,255,0.5)',
    transition: 'all 0.25s ease',
  },

  neonDisabled: {
    opacity: 0.5,
    boxShadow: 'none',
    cursor: 'default',
  },

  favoriteBtn: {
    padding: '6px 10px',
    border: '1px solid #ff4fd8',
    borderRadius: '6px',
    cursor: 'pointer',
    color: '#ff4fd8',
    textShadow: '0 0 6px rgba(255,79,216,0.7)',
    boxShadow: '0 0 8px rgba(255,79,216,0.5)',
    transition: 'all 0.25s ease',
  },

  favoriteActive: {
    color: '#ff9de8',
    boxShadow: '0 0 12px rgba(255,79,216,0.9)',
  },
};

export default ProductCard;
