import { useMemo, useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import './Catalog.css';

const SORT_OPTIONS = {
  age: 'Newest',
  title: 'Alphabetically',
  price: 'Cheapest',
};

function Catalog({
  products,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const [sort, setSort] = useState('age');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // 游댳 SIMULATED LOADING (햪혪햡햨, 햠햣향 햣햣향햟햡혞향쮏)
  useEffect(() => {
    setLoading(true);
    setError(false);

    const timer = setTimeout(() => {
      if (!products) {
        setError(true);
      }
      setLoading(false);
    }, 300);

    return () => clearTimeout(timer);
  }, [products]);

  // 游댳 SORT
  const sortedProducts = useMemo(() => {
    const prepared = [...products];

    switch (sort) {
      case 'title':
        return prepared.sort((a, b) =>
          a.name.localeCompare(b.name)
        );

      case 'price':
        return prepared.sort(
          (a, b) => a.priceDiscount - b.priceDiscount
        );

      case 'age':
      default:
        return prepared.sort((a, b) => b.year - a.year);
    }
  }, [products, sort]);

  // 游댳 STATES
  if (loading) {
    return <h2 className="neon-text">Loading...</h2>;
  }

  if (error) {
    return (
      <div>
        <h2>Something went wrong</h2>
        <button onClick={() => window.location.reload()}>
          Reload
        </button>
      </div>
    );
  }

  if (!products.length) {
    return <h2>There are no products yet</h2>;
  }

  return (
    <section className="catalog">
      <h1 className="catalog-title">Products</h1>

      {/* 游댳 CONTROLS */}
      <div className="catalog-controls">
        <label className="neon-label">
          Sort by:{' '}
          <select
            className="neon-select"
            value={sort}
            onChange={(e) => setSort(e.target.value)}
          >
            {Object.entries(SORT_OPTIONS).map(([key, label]) => (
              <option key={key} value={key}>
                {label}
              </option>
            ))}
          </select>
        </label>
      </div>

      {/* 游댳 GRID */}
      <div className="catalog-grid">
        {sortedProducts.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            cart={cart}
            favorites={favorites}
            addToCart={addToCart}
            addToFavorites={addToFavorites}
          />
        ))}
      </div>
    </section>
  );
}

export default Catalog;
