function ProductCard({ product, onAddToCart, onAddToFavorites }) {
  return (
    <div className="product-card">
      <div className="product-image">
        {product.images?.[0] && (
          <img
            src={`/${product.images[0]}`}
            alt={product.name}
          />
        )}
      </div>

      <h3 className="product-title">{product.name}</h3>
      <p className="product-category">{product.category}</p>
      <p className="product-price">${product.priceDiscount}</p>

      <div className="product-actions">
        <button className="neon-text-btn" onClick={() => onAddToCart(product)}>
          Add to cart
        </button>
        <button className="neon-text-btn heart" onClick={() => onAddToFavorites(product)}>
          â™¥ Favorite
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
