import { Link } from 'react-router-dom';

function Cart({ cart, removeFromCart }) {
if (cart.length === 0) {
return (
<div className="catalog">
<h1 className="catalog-title">ðŸ›’ Cart</h1>
<p>Your cart is empty.</p>
</div>
);
}

const total = cart.reduce(
(sum, item) => sum + item.priceDiscount,
0
);

return (
<div className="catalog">
<h1 className="catalog-title">ðŸ›’ Cart</h1>

{cart.map(product => (
<div key={product.configId} className="product-card">
<Link
to={`/${product.category}/${product.id}`}
className="product-link"
>
<h3 className="product-title">{product.name}</h3>
</Link>

{/* ðŸ”¹ VARIANTS */}
<p className="product-category">
Color: {product.selectedColor}
</p>

{product.selectedCapacity && (
<p className="product-category">
{product.category === 'accessories'
? `Screen: ${product.selectedCapacity}`
: `Capacity: ${product.selectedCapacity}`}
</p>
)}

{/* ðŸ”¹ DESCRIPTION */}
{product.description?.[0]?.text?.[0] && (
<p style={{ opacity: 0.7, fontSize: 13, marginTop: 6 }}>
{product.description[0].text[0]}
</p>
)}

<p className="product-price">
${product.priceDiscount}
</p>

<div className="product-actions">
<button
type="button"
className="neon-text-btn"
onClick={() => removeFromCart(product.configId)}
>
âœ• Remove
</button>
</div>
</div>
))}

<h3 style={{ marginTop: 24 }}>
Total: ${total}
</h3>
</div>
);
}

export default Cart;
