import { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';

import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [activeImage, setActiveImage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    setLoading(true);
    setError(false);

    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    const load = loaders[category];

    if (!load) {
      setError(true);
      setLoading(false);
      return;
    }

    load()
      .then(items => {
        const found = items.find(item => item.id === productId);

        if (!found) {
          setError(true);
        } else {
          setProduct(found);
          setActiveImage(found.images[0]);
        }
      })
      .catch(() => setError(true))
      .finally(() => setLoading(false));
  }, [category, productId]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error || !product) {
    return <h2>Product was not found</h2>;
  }

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div style={{ padding: '16px' }}>
      {/* Breadcrumbs */}
      <div style={{ marginBottom: '12px', fontSize: '14px' }}>
        <Link to="/">Home</Link>
        {' / '}
        <Link to={`/${product.category}`}>
          {product.category.charAt(0).toUpperCase() + product.category.slice(1)}
        </Link>
        {' / '}
        <span>{product.name}</span>
      </div>

      {/* Back */}
      <button onClick={() => navigate(-1)}>← Back</button>

      <h1>{product.name}</h1>

      {/* Image gallery */}
      <div style={{ display: 'flex', gap: '16px', marginBottom: '16px' }}>
        <div>
          <img
            src={activeImage}
            alt={product.name}
            style={{ width: '300px', objectFit: 'contain' }}
          />
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
          {product.images.map(img => (
            <img
              key={img}
              src={img}
              alt={product.name}
              style={{
                width: '60px',
                cursor: 'pointer',
                border: img === activeImage ? '2px solid black' : '1px solid #ccc',
              }}
              onClick={() => setActiveImage(img)}
            />
          ))}
        </div>
      </div>

      <p><strong>Price:</strong> ${product.priceDiscount}</p>

      {/* Actions */}
      <div style={{ display: 'flex', gap: '8px', marginBottom: '24px' }}>
        <button
          onClick={() => addToCart(product)}
          disabled={isInCart}
        >
          {isInCart ? '✓ Added to cart' : 'Add to cart'}
        </button>

        <button
          onClick={() => addToFavorites(product)}
          disabled={isFavorite}
        >
          {isFavorite ? '♥ In favorites' : '♡ Add to favorites'}
        </button>
      </div>

      {/* About */}
      <h3>About</h3>
      {product.description.map(section => (
        <div key={section.title}>
          <h4>{section.title}</h4>
          {section.text.map(line => (
            <p key={line}>{line}</p>
          ))}
        </div>
      ))}

      {/* Tech specs */}
      <h3>Tech specs</h3>
      <ul>
        <li>Screen: {product.screen}</li>
        <li>Resolution: {product.resolution}</li>
        <li>Processor: {product.processor}</li>
        <li>RAM: {product.ram}</li>
      </ul>
    </div>
  );
}

export default ProductDetails;
