import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  /* ================= LOAD PRODUCT ================= */
  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]()
      .then(items => {
        const found = items.find(p => p.id === productId);
        setProduct(found || null);
      })
      .finally(() => setLoading(false));
  }, [category, productId]);

  if (loading) {
    return <div className="page">Loading...</div>;
  }

  if (!product) {
    return <div className="page">Product not found</div>;
  }

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      {/* ================= MAIN GRID ================= */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: 48,
          marginTop: 32,
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            borderRadius: 16,
            background:
              'radial-gradient(circle at top, rgba(120,90,255,0.25), rgba(10,12,30,0.9))',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {product.image && (
            <img
              src={product.image}
              alt={product.name}
              style={{ maxWidth: '85%', maxHeight: '85%' }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${product.price}
          </p>

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className={`neon-btn ${isInCart ? 'active' : ''}`}
              disabled={isInCart}
              onClick={() => addToCart(product)}
            >
              {isInCart ? '✓ Added' : 'Add to cart'}
            </button>

            <button
              className={`neon-btn ${isFavorite ? 'active' : ''}`}
              onClick={() => !isFavorite && addToFavorites(product)}
            >
              {isFavorite ? '♥ Favorited' : '♡ Favorite'}
            </button>
          </div>
        </div>
      </div>

      {/* ================= DESCRIPTION ================= */}
      {product.description && (
        <div style={{ marginTop: 64, maxWidth: 960 }}>
          <h2 className="neon-text" style={{ marginBottom: 24 }}>
            Description
          </h2>

          {product.description.map(section => (
            <div key={section.title} style={{ marginBottom: 32 }}>
              <h3
                style={{
                  fontSize: 18,
                  marginBottom: 12,
                  textShadow: '0 0 8px rgba(255,160,220,0.6)',
                }}
              >
                {section.title}
              </h3>

              {section.text.map((paragraph, idx) => (
                <p
                  key={idx}
                  style={{
                    opacity: 0.75,
                    lineHeight: 1.6,
                    marginBottom: 8,
                  }}
                >
                  {paragraph}
                </p>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ProductDetails;
