import { useState } from 'react';

function ProductCard({ product, addToCart }) {
  const [added, setAdded] = useState(false);

  const handleAdd = () => {
    addToCart(product);
    setAdded(true);
  };

  return (
    <div style={styles.card}>
      <img
        src={product.image}
        alt={product.name}
        style={styles.image}
      />

      <h3>{product.name}</h3>
      <p>{product.category}</p>
      <p>${product.price}</p>

      <div style={styles.actions}>
        <button
          onClick={handleAdd}
          disabled={added}
        >
          {added ? 'Added ✓' : 'Add to cart'}
        </button>

        <button>♡ Favorite</button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    border: '1px solid #333',
    borderRadius: '8px',
    padding: '16px',
    width: '200px',
  },
  image: {
    width: '100%',
    marginBottom: '8px',
  },
  actions: {
    display: 'flex',
    gap: '8px',
  },
};

export default ProductCard;
