import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { getPhones, getTablets, getAccessories } from '../api/productsApi';

function ProductDetails({
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]()
      .then(items => {
        const found = items.find(p => p.id === productId);
        setProduct(found || null);
      })
      .finally(() => setLoading(false));
  }, [category, productId]);

  if (loading) {
    return <div className="page">Loading...</div>;
  }

  if (!product) {
    return <div className="page">Product was not found</div>;
  }

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: '48px',
          marginTop: '32px',
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            borderRadius: 16,
            background:
              'radial-gradient(circle at top, rgba(120,90,255,0.25), rgba(10,12,30,0.9))',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <img
            src={product.images?.[0]}
            alt={product.name}
            style={{
              maxWidth: '85%',
              maxHeight: '85%',
              objectFit: 'contain',
            }}
          />
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.7 }}>{product.category}</p>

          <p
            className="neon-text"
            style={{ fontSize: 24, margin: '16px 0' }}
          >
            ${product.priceDiscount}
          </p>

          {/* COLORS */}
          {product.colorsAvailable && (
            <>
              <h3>Available colors</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.colorsAvailable.map(color => (
                  <div
                    key={color}
                    title={color}
                    style={{
                      width: 22,
                      height: 22,
                      borderRadius: '50%',
                      background: color,
                      boxShadow:
                        '0 0 8px rgba(255,255,255,0.4)',
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {/* CAPACITY */}
          {product.capacityAvailable && (
            <>
              <h3 style={{ marginTop: 24 }}>Capacity</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.capacityAvailable.map(cap => (
                  <div
                    key={cap}
                    style={{
                      padding: '6px 12px',
                      borderRadius: 8,
                      background: 'rgba(255,255,255,0.05)',
                      boxShadow:
                        '0 0 6px rgba(180,140,255,0.4)',
                    }}
                  >
                    {cap}
                  </div>
                ))}
              </div>
            </>
          )}

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className="neon-btn"
              disabled={isInCart}
              onClick={() => addToCart(product)}
            >
              {isInCart ? 'Added to cart' : 'Add to cart'}
            </button>

            <button
              className="neon-btn"
              onClick={() => !isFavorite && addToFavorites(product)}
            >
              {isFavorite ? '♥ Favorite' : '♡ Favorite'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
