import { Link } from 'react-router-dom';

function Cart({ cart, removeFromCart }) {
if (!cart.length) {
return (
<div className="catalog">
<h1 className="catalog-title">ðŸ›’ Cart</h1>
<p>Your cart is empty.</p>
</div>
);
}

const total = cart.reduce((sum, item) => sum + item.priceDiscount, 0);

return (
<div className="catalog">
<h1 className="catalog-title">ðŸ›’ Cart</h1>

{cart.map(item => (
<div key={item.configId} className="product-card">
<Link
to={`/${item.category}/${item.productId}`}
className="product-link"
>
<h3 className="product-title">
{item.name} â€” {item.selectedColor} / {item.selectedOption}
</h3>
</Link>

<p className="product-price">${item.priceDiscount}</p>

<div className="product-actions">
<button
className="neon-text-btn"
onClick={() => removeFromCart(item.configId)}
>
âœ• Remove
</button>
</div>
</div>
))}

<h3 style={{ marginTop: 24 }}>Total: ${total}</h3>
</div>
);
}

export default Cart;
