function Favorites({ favorites, removeFromFavorites }) {
  const total = favorites.reduce((sum, item) => sum + item.priceDiscount, 0);

  return (
    <section className="favorites">
      <h1>❤️ Favorites</h1>

      {favorites.length === 0 && <p>No favorites yet</p>}

      {favorites.map(product => (
        <div key={product.id} className="favorites-item">
          <span>
            {product.name} — ${product.priceDiscount}
          </span>

          <button
            className="neon-text-btn heart"
            onClick={() => removeFromFavorites(product.id)}
          >
            ✕ Remove
          </button>
        </div>
      ))}

      {favorites.length > 0 && (
        <h3>Total: ${total}</h3>
      )}
    </section>
  );
}

export default Favorites;
