function ProductCard({ product, cart, addToCart, addToFavorites }) {
  const isInCart = cart.some(item => item.id === product.id);

  return (
    <div style={styles.card}>
      <img
        src={product.images?.[0]}
        alt={product.name}
        style={styles.image}
      />

      <h3>{product.name}</h3>

      <p style={styles.category}>{product.category}</p>

      <p style={styles.price}>
        ${product.priceDiscount}
      </p>

      <div style={styles.actions}>
        <button
          onClick={() => addToCart(product)}
          disabled={isInCart}
        >
          {isInCart ? '✓ Added' : 'Add to cart'}
        </button>

        <button onClick={() => addToFavorites(product)}>
          ♡ Favorite
        </button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: '220px',
    padding: '12px',
    border: '1px solid #ddd',
    borderRadius: '8px',
  },
  image: {
    width: '100%',
    height: '140px',
    objectFit: 'contain',
  },
  category: {
    fontSize: '12px',
    opacity: 0.7,
  },
  price: {
    fontWeight: 'bold',
    margin: '8px 0',
  },
  actions: {
    display: 'flex',
    gap: '8px',
  },
};

export default ProductCard;
