function Favorites({ favorites, removeFromFavorites }) {
  if (favorites.length === 0) {
    return <h2>❤️ Favorites is empty</h2>;
  }

  const total = favorites.reduce((sum, item) => {
    return sum + item.priceDiscount;
  }, 0);

  return (
    <div>
      <h2>❤️ Favorites</h2>

      <ul>
        {favorites.map(item => (
          <li
            key={item.id}
            style={{
              marginBottom: '8px',
              display: 'flex',
              justifyContent: 'space-between',
              maxWidth: '500px',
            }}
          >
            <span>
              {item.name} — ${item.priceDiscount}
            </span>

            <button onClick={() => removeFromFavorites(item.id)}>
              Remove
            </button>
          </li>
        ))}
      </ul>

      <h3>Total: ${total}</h3>
    </div>
  );
}

export default Favorites;
