import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [variants, setVariants] = useState([]);
  const [product, setProduct] = useState(null);

  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedCapacity, setSelectedCapacity] = useState(null);

  /* ================= LOAD & GROUP ================= */
  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      // базовый namespace (apple-iphone-11)
      const baseId = productId.split('-').slice(0, -2).join('-');

      const grouped = items.filter(item =>
        item.itemId.startsWith(baseId)
      );

      if (!grouped.length) return;

      setVariants(grouped);

      const first = grouped[0];
      setProduct(first);
      setSelectedColor(first.color);
      setSelectedCapacity(first.capacity);
    });
  }, [category, productId]);

  if (!product) return <div className="page">Loading...</div>;

  /* ================= DERIVED ================= */
  const colors = [...new Set(variants.map(v => v.color))];
  const capacities = [...new Set(variants.map(v => v.capacity))];

  const currentVariant = variants.find(
    v => v.color === selectedColor && v.capacity === selectedCapacity
  );

  const image = currentVariant
    ? `/${currentVariant.image}`
    : null;

  const price = currentVariant?.price;

  const configId = currentVariant?.itemId;

  const isInCart = cart.some(i => i.configId === configId);
  const isFavorite = favorites.some(i => i.configId === configId);

  const preparedProduct = {
    ...currentVariant,
    configId,
    image,
    priceDiscount: price,
  };

  /* ================= UI ================= */
  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: 48,
          marginTop: 32,
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            borderRadius: 16,
            background:
              'radial-gradient(circle at top, rgba(120,90,255,0.25), rgba(10,12,30,0.9))',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {image && (
            <img
              src={image}
              alt={product.name}
              style={{ maxWidth: '85%', maxHeight: '85%' }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${price}
          </p>

          {/* COLORS */}
          <h3>Available colors</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {colors.map(color => (
              <button
                key={color}
                onClick={() => setSelectedColor(color)}
                style={{
                  width: 22,
                  height: 22,
                  borderRadius: '50%',
                  border: 'none',
                  cursor: 'pointer',
                  background: color,
                  boxShadow:
                    color === selectedColor
                      ? '0 0 10px rgba(255,160,220,1)'
                      : '0 0 6px rgba(255,255,255,0.4)',
                }}
              />
            ))}
          </div>

          {/* CAPACITY */}
          <h3 style={{ marginTop: 24 }}>Capacity</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {capacities.map(cap => (
              <button
                key={cap}
                className="neon-btn"
                onClick={() => setSelectedCapacity(cap)}
                style={{
                  opacity: cap === selectedCapacity ? 1 : 0.5,
                }}
              >
                {cap}
              </button>
            ))}
          </div>

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className={`neon-btn ${isInCart ? 'active' : ''}`}
              disabled={isInCart}
              onClick={() => addToCart(preparedProduct)}
            >
              {isInCart ? '✓ Added' : 'Add to cart'}
            </button>

            <button
              className={`neon-btn ${isFavorite ? 'active' : ''}`}
              onClick={() => !isFavorite && addToFavorites(preparedProduct)}
            >
              {isFavorite ? '♥ Favorited' : '♡ Favorite'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
