import { useSearchParams } from 'react-router-dom';
import { useMemo, useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';

const SORT_OPTIONS = {
  age: 'Newest',
  title: 'Alphabetically',
  price: 'Cheapest',
};

const PER_PAGE_OPTIONS = [4, 8, 16, 'all'];

function Catalog({
  products,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const [searchParams, setSearchParams] = useSearchParams();

  const [loading, setLoading] = useState(true);

  const sort = searchParams.get('sort') || 'age';
  const page = Number(searchParams.get('page') || 1);
  const perPage = searchParams.get('perPage') || 'all';

  // â³ FAKE LOADING (Ð±ÐµÐ· Ð¿ÐµÑ€ÐµÐ·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñ‹)
  useEffect(() => {
    setLoading(true);
    const t = setTimeout(() => setLoading(false), 300);
    return () => clearTimeout(t);
  }, [sort, page, perPage]);

  // ðŸ”¹ SORT
  const sortedProducts = useMemo(() => {
    const list = [...products];

    switch (sort) {
      case 'title':
        return list.sort((a, b) => a.name.localeCompare(b.name));
      case 'price':
        return list.sort((a, b) => a.priceDiscount - b.priceDiscount);
      default:
        return list.sort((a, b) => b.year - a.year);
    }
  }, [products, sort]);

  // ðŸ”¹ PAGINATION
  const visibleProducts = useMemo(() => {
    if (perPage === 'all') {
      return sortedProducts;
    }

    const start = (page - 1) * perPage;
    return sortedProducts.slice(start, start + Number(perPage));
  }, [sortedProducts, page, perPage]);

  const totalPages =
    perPage === 'all'
      ? 1
      : Math.ceil(sortedProducts.length / perPage);

  // ðŸ”¹ HANDLERS (Ð‘Ð•Ð— reload)
  const handleSortChange = e => {
    setSearchParams({ sort: e.target.value, perPage, page: '1' });
  };

  const handlePerPageChange = e => {
    const value = e.target.value;

    setSearchParams(
      value === 'all'
        ? { sort }
        : { sort, perPage: value, page: '1' }
    );
  };

  const handlePageChange = newPage => {
    setSearchParams({ sort, perPage, page: String(newPage) });
  };

  if (loading) {
    return <div className="page neon-text">Loadingâ€¦</div>;
  }

  return (
    <div className="page">
      <h1 className="neon-text">Products</h1>

      {/* CONTROLS */}
      <div
        style={{
          display: 'flex',
          gap: 24,
          marginBottom: 24,
          alignItems: 'center',
        }}
      >
        <label className="neon-text">
          Sort by:{' '}
          <select value={sort} onChange={handleSortChange}>
            {Object.entries(SORT_OPTIONS).map(([key, label]) => (
              <option key={key} value={key}>
                {label}
              </option>
            ))}
          </select>
        </label>

        <label className="neon-text">
          Items on page:{' '}
          <select value={perPage} onChange={handlePerPageChange}>
            {PER_PAGE_OPTIONS.map(option => (
              <option key={option} value={option}>
                {option}
              </option>
            ))}
          </select>
        </label>
      </div>

      {/* GRID */}
      <div className="products-grid">
        {visibleProducts.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            cart={cart}
            favorites={favorites}
            addToCart={addToCart}
            addToFavorites={addToFavorites}
          />
        ))}
      </div>

      {/* PAGINATION */}
      {totalPages > 1 && (
        <div style={{ marginTop: 32, display: 'flex', gap: 16 }}>
          {Array.from({ length: totalPages }, (_, i) => i + 1).map(p => (
            <button
              key={p}
              type="button"
              className="neon-btn"
              onClick={() => handlePageChange(p)}
              style={{
                opacity: p === page ? 1 : 0.4,
                textShadow:
                  p === page
                    ? '0 0 8px rgba(255,160,220,0.9)'
                    : undefined,
              }}
            >
              {p}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

export default Catalog;
