import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import { getPhones, getTablets, getAccessories } from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      setProducts(items);
      const found = items.find(p => p.id === productId);
      setProduct(found || null);
      setLoading(false);
    });
  }, [category, productId]);

  if (loading) return <div className="page">Loading...</div>;
  if (!product) return <div className="page">Product not found</div>;

  /* ---------- VARIANTS ---------- */
  const variants = products.filter(
    p => p.namespaceId === product.namespaceId
  );

  const colors = [...new Set(variants.map(v => v.color))];
  const capacities = [...new Set(variants.map(v => v.capacity))];

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  /* ---------- HANDLERS ---------- */
  const changeColor = color => {
    const next = variants.find(
      v => v.color === color && v.capacity === product.capacity
    );
    if (next) {
      navigate(`/${category}/${next.id}`);
    }
  };

  const changeCapacity = capacity => {
    const next = variants.find(
      v => v.capacity === capacity && v.color === product.color
    );
    if (next) {
      navigate(`/${category}/${next.id}`);
    }
  };

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: 48,
          marginTop: 32,
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            borderRadius: 16,
            background:
              'radial-gradient(circle at top, rgba(120,90,255,0.25), rgba(10,12,30,0.9))',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <img
            src={product.images[0]}
            alt={product.name}
            style={{ maxWidth: '85%', maxHeight: '85%' }}
          />
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${product.priceDiscount}
          </p>

          {/* COLORS */}
          <h3>Available colors</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {colors.map(color => (
              <button
                key={color}
                onClick={() => changeColor(color)}
                style={{
                  width: 22,
                  height: 22,
                  borderRadius: '50%',
                  border: 'none',
                  cursor: 'pointer',
                  background: color,
                  boxShadow:
                    color === product.color
                      ? '0 0 10px rgba(255,160,220,1)'
                      : '0 0 6px rgba(255,255,255,0.4)',
                }}
              />
            ))}
          </div>

          {/* CAPACITY */}
          <h3 style={{ marginTop: 24 }}>Capacity</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {capacities.map(cap => (
              <button
                key={cap}
                className="neon-btn"
                onClick={() => changeCapacity(cap)}
                style={{
                  opacity: cap === product.capacity ? 1 : 0.5,
                }}
              >
                {cap}
              </button>
            ))}
          </div>

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className="neon-btn"
              disabled={isInCart}
              onClick={() => addToCart(product)}
            >
              {isInCart ? '✓ Added' : 'Add to cart'}
            </button>

            <button
              className="neon-btn"
              onClick={() => !isFavorite && addToFavorites(product)}
            >
              {isFavorite ? '♥ Favorited' : '♡ Favorite'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
