import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import {
getPhones,
getTablets,
getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
const { category, productId } = useParams();
const navigate = useNavigate();

const [product, setProduct] = useState(null);
const [loading, setLoading] = useState(true);

const [selectedColor, setSelectedColor] = useState(null);
const [selectedOption, setSelectedOption] = useState(null);

useEffect(() => {
const loaders = {
phones: getPhones,
tablets: getTablets,
accessories: getAccessories,
};

loaders[category]().then(items => {
const found = items.find(p => p.id === productId);
setProduct(found || null);

if (found) {
setSelectedColor(found.color || found.colorsAvailable?.[0] || null);
setSelectedOption(
found.capacity ||
found.screen ||
found.capacityAvailable?.[0] ||
null
);
}

setLoading(false);
});
}, [category, productId]);

const configId = useMemo(() => {
return `${product?.id}-${selectedColor}-${selectedOption}`;
}, [product, selectedColor, selectedOption]);

const computedPrice = useMemo(() => {
if (!product || !selectedOption || !product.capacityAvailable) {
return product?.priceDiscount;
}

const baseIndex = product.capacityAvailable.indexOf(product.capacity);
const currentIndex = product.capacityAvailable.indexOf(selectedOption);

return product.priceDiscount + (currentIndex - baseIndex) * 100;
}, [product, selectedOption]);

const computedImage = useMemo(() => {
if (!product || !selectedColor || !product.images?.length) {
return null;
}

const base = product.images[0].split(`/${product.color}/`)[0];
return `/${base}/${selectedColor}/00.webp`;
}, [product, selectedColor]);

if (loading) return <div className="page">Loading...</div>;
if (!product) return <div className="page">Product not found</div>;

const isInCart = cart.some(item => item.configId === configId);
const isFavorite = favorites.some(item => item.configId === configId);

const preparedProduct = {
configId,
productId: product.id,
name: product.name,
category: product.category,
selectedColor,
selectedOption,
priceDiscount: computedPrice,
image: computedImage,
};

return (
<div className="page">
<button className="neon-btn" onClick={() => navigate(-1)}>
← Back
</button>

<h1 className="neon-text" style={{ marginTop: 24 }}>
{product.name}
</h1>

<div
style={{
display: 'grid',
gridTemplateColumns: '360px 1fr',
gap: 48,
marginTop: 32,
}}
>
<div style={{ display: 'flex', justifyContent: 'center' }}>
{computedImage && (
<img
src={computedImage}
alt={product.name}
style={{
maxWidth: 260,
filter:
'drop-shadow(0 0 12px rgba(255,255,255,0.6)) drop-shadow(0 0 28px rgba(255,255,255,0.35))',
}}
/>
)}
</div>

<div>
<p style={{ opacity: 0.6 }}>{product.category}</p>

<p className="neon-text" style={{ fontSize: 24 }}>
${computedPrice}
</p>

{product.colorsAvailable && (
<>
<h3>Color</h3>
<div style={{ display: 'flex', gap: 12 }}>
{product.colorsAvailable.map(color => (
<button
key={color}
onClick={() => setSelectedColor(color)}
style={{
width: 22,
height: 22,
borderRadius: '50%',
background: color,
border: 'none',
boxShadow:
color === selectedColor
? '0 0 10px #fff'
: '0 0 4px rgba(255,255,255,0.4)',
}}
/>
))}
</div>
</>
)}

{product.capacityAvailable && (
<>
<h3 style={{ marginTop: 24 }}>Capacity</h3>
<div style={{ display: 'flex', gap: 12 }}>
{product.capacityAvailable.map(opt => (
<button
key={opt}
className="neon-btn"
onClick={() => setSelectedOption(opt)}
style={{ opacity: opt === selectedOption ? 1 : 0.5 }}
>
{opt}
</button>
))}
</div>
</>
)}

<div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
<button
className="neon-btn"
disabled={isInCart}
onClick={() => addToCart(preparedProduct)}
>
{isInCart ? '✓ Added' : 'Add to cart'}
</button>

<button
className="neon-btn"
onClick={() => !isFavorite && addToFavorites(preparedProduct)}
>
{isFavorite ? '♥ Favorited' : '♡ Favorite'}
</button>
</div>
</div>
</div>
</div>
);
}

export default ProductDetails;
