import { useParams, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const { category, productId } = useParams();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let loader;

    if (category === 'phones') loader = getPhones;
    if (category === 'tablets') loader = getTablets;
    if (category === 'accessories') loader = getAccessories;

    loader().then(data => {
      const found = data.find(item => item.id === productId);
      setProduct(found);
      setLoading(false);
    });
  }, [category, productId]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!product) {
    return <p>Product not found</p>;
  }

  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div style={{ padding: 20 }}>
      <Link to={`/${category}`}>← Back</Link>

      <h1>{product.name}</h1>

      <img
        src={product.images?.[0]}
        alt={product.name}
        style={{ width: '300px', marginBottom: '16px' }}
      />

      <p><strong>Price:</strong> ${product.priceDiscount}</p>

      <p>{product.description?.[0]?.text}</p>

      <div style={{ marginTop: '16px' }}>
        <button
          onClick={() => addToCart(product)}
          disabled={isInCart}
        >
          {isInCart ? '✓ Added' : 'Add to cart'}
        </button>

        <button
          onClick={() => addToFavorites(product)}
          disabled={isFavorite}
          style={{ marginLeft: '8px' }}
        >
          {isFavorite ? '♥ In favorites' : '♡ Add to favorites'}
        </button>
      </div>
    </div>
  );
}

export default ProductDetails;ф
