import { NavLink } from 'react-router-dom';

function Header() {
  return (
    <header style={styles.header}>
      <div style={styles.logo}>âš¡ Phone Catalog</div>

      <nav style={styles.nav}>
        {[
          ['/', 'Home'],
          ['/phones', 'Phones'],
          ['/tablets', 'Tablets'],
          ['/accessories', 'Accessories'],
          ['/cart', 'Cart'],
          ['/favorites', 'Favorites'],
        ].map(([to, label]) => (
          <NavLink
            key={to}
            to={to}
            style={({ isActive }) => ({
              ...styles.link,
              color: isActive ? 'var(--neon-pink)' : 'var(--neon-white)',
              textShadow: isActive
                ? '0 0 8px var(--neon-pink)'
                : '0 0 6px rgba(255,255,255,0.3)',
            })}
          >
            {label}
          </NavLink>
        ))}
      </nav>
    </header>
  );
}

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px 40px',
    borderBottom: '1px solid rgba(255,255,255,0.1)',
  },
  logo: {
    fontWeight: 500,
    letterSpacing: '1px',
  },
  nav: {
    display: 'flex',
    gap: '24px',
  },
  link: {
    fontSize: '14px',
    transition: 'color 0.3s, text-shadow 0.3s',
  },
};

export default Header;
