import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import { getPhones, getTablets, getAccessories } from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedCapacity, setSelectedCapacity] = useState(null);

  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      const found = items.find(p => p.id === productId);
      setProduct(found || null);

      if (found) {
        setSelectedColor(found.colorsAvailable?.[0] || null);
        setSelectedCapacity(found.capacityAvailable?.[0] || null);
      }

      setLoading(false);
    });
  }, [category, productId]);

  const computedPrice = useMemo(() => {
    if (!product || !selectedCapacity) return product?.priceDiscount;

    const baseIndex = product.capacityAvailable.indexOf(product.capacity);
    const currentIndex = product.capacityAvailable.indexOf(selectedCapacity);

    return product.priceDiscount + (currentIndex - baseIndex) * 100;
  }, [product, selectedCapacity]);

  const computedImage = useMemo(() => {
    if (!product) return null;

    return product.images?.find(img =>
      img.includes(`/${selectedColor}/`)
    ) || product.images?.[0];
  }, [product, selectedColor]);

  if (loading) return <div className="page">Loading...</div>;
  if (!product) return <div className="page">Product not found</div>;

  const configId = `${product.id}-${selectedColor}-${selectedCapacity}`;

  const isInCart = cart.some(item => item.configId === configId);
  const isFavorite = favorites.some(item => item.configId === configId);

  const preparedProduct = {
    ...product,
    configId,
    selectedColor,
    selectedCapacity,
    priceDiscount: computedPrice,
  };

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: 48,
          marginTop: 32,
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            borderRadius: 16,
            background:
              'radial-gradient(circle at top, rgba(120,90,255,0.25), rgba(10,12,30,0.9))',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {computedImage && (
            <img
              src={`/${computedImage}`}
              alt={product.name}
              style={{ maxWidth: '85%', maxHeight: '85%' }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${computedPrice}
          </p>

          {/* COLORS */}
          <h3>Available colors</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {product.colorsAvailable.map(color => (
              <button
                key={color}
                onClick={() => setSelectedColor(color)}
                style={{
                  width: 22,
                  height: 22,
                  borderRadius: '50%',
                  border: 'none',
                  cursor: 'pointer',
                  background: color,
                  boxShadow:
                    color === selectedColor
                      ? '0 0 10px rgba(255,160,220,1)'
                      : '0 0 6px rgba(255,255,255,0.4)',
                }}
              />
            ))}
          </div>

          {/* CAPACITY */}
          <h3 style={{ marginTop: 24 }}>Capacity</h3>
          <div style={{ display: 'flex', gap: 12 }}>
            {product.capacityAvailable.map(cap => (
              <button
                key={cap}
                className="neon-btn"
                onClick={() => setSelectedCapacity(cap)}
                style={{
                  padding: '6px 12px',
                  opacity: cap === selectedCapacity ? 1 : 0.5,
                }}
              >
                {cap}
              </button>
            ))}
          </div>

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className="neon-btn"
              disabled={isInCart}
              onClick={() => addToCart(preparedProduct)}
            >
              {isInCart ? '✓ Added' : 'Add to cart'}
            </button>

            <button
              className="neon-btn"
              disabled={isFavorite}
              onClick={() => addToFavorites(preparedProduct)}
            >
              {isFavorite ? '♥ Favorited' : '♡ Favorite'}
            </button>
          </div>
        </div>
      </div>

      {/* DESCRIPTION */}
      <div style={{ marginTop: 64 }}>
        {product.description?.map(section => (
          <div key={section.title} style={{ marginBottom: 32 }}>
            <h2>{section.title}</h2>
            {section.text.map((p, i) => (
              <p key={i} style={{ opacity: 0.8 }}>
                {p}
              </p>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductDetails;
