import { Link } from 'react-router-dom';

function ProductCard({
  product,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (!isInCart) {
      addToCart(product);
    }
  };

  const handleAddToFavorites = (e) => {
    e.preventDefault();
    e.stopPropagation();
    addToFavorites(product);
  };

  return (
    <div className="product-card">
      <Link to={`/${product.category}/${product.id}`} className="product-link">
        <div className="product-image">
          {product.images?.[0] && (
            <img src={product.images[0]} alt={product.name} />
          )}
        </div>
        <h3 className="product-title">{product.name}</h3>
      </Link>

      <p className="product-category">{product.category}</p>
      <p className="product-price">${product.priceDiscount}</p>

      <div className="product-actions">
        <span
          role="button"
          tabIndex={0}
          className={`neon-text-btn ${isInCart ? 'active disabled' : ''}`}
          onClick={handleAddToCart}
          onKeyDown={(e) => e.key === 'Enter' && handleAddToCart(e)}
        >
          {isInCart ? '✓ Added' : 'Add to cart'}
        </span>

        <span
          role="button"
          tabIndex={0}
          className={`neon-text-btn heart ${isFavorite ? 'active' : ''}`}
          onClick={handleAddToFavorites}
          onKeyDown={(e) => e.key === 'Enter' && handleAddToFavorites(e)}
        >
          {isFavorite ? '♥ Favorited' : '♡ Favorite'}
        </span>
      </div>
    </div>
  );
}

export default ProductCard;
