import { Link } from 'react-router-dom';
import './ProductCard.css';

function ProductCard({
  product,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div className="product-card">
      {/* IMAGE */}
      <Link to={`/${product.category}/${product.id}`} className="product-image">
        <img
          src={product.images?.[0]}
          alt={product.name}
        />
      </Link>

      {/* INFO */}
      <div className="product-info">
        <h3 className="product-title">{product.name}</h3>
        <p className="product-category">{product.category}</p>
        <p className="product-price">${product.priceDiscount}</p>
      </div>

      {/* ACTIONS */}
      <div className="product-actions">
        <button
          className={`neon-text-btn ${isInCart ? 'active' : ''}`}
          onClick={() => !isInCart && addToCart(product)}
        >
          {isInCart ? 'Added' : 'Add to cart'}
        </button>

        <button
          className={`neon-text-btn heart ${isFavorite ? 'active' : ''}`}
          onClick={() => !isFavorite && addToFavorites(product)}
        >
          â™¥ Favorite
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
