import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedCapacity, setSelectedCapacity] = useState(null);

  /* ================= LOAD PRODUCT ================= */
  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      const found = items.find(p => p.id === productId);
      setProduct(found || null);

      if (found) {
        setSelectedColor(found.color || found.colorsAvailable?.[0] || null);
        setSelectedCapacity(found.capacity || found.capacityAvailable?.[0] || null);
      }

      setLoading(false);
    });
  }, [category, productId]);

  /* ================= PRICE ================= */
  const computedPrice = useMemo(() => {
    if (!product || !selectedCapacity) return product?.priceDiscount;

    const baseIndex = product.capacityAvailable.indexOf(product.capacity);
    const currentIndex = product.capacityAvailable.indexOf(selectedCapacity);

    return product.priceDiscount + (currentIndex - baseIndex) * 100;
  }, [product, selectedCapacity]);

  /* ================= IMAGE (BY COLOR) ================= */
  const computedImage = useMemo(() => {
    if (!product || !selectedColor) return null;

    return (
      product.images?.find(img => img.includes(`/${selectedColor}/`)) ||
      product.images?.[0] ||
      null
    );
  }, [product, selectedColor]);

  if (loading) return <div className="page">Loading...</div>;
  if (!product) return <div className="page">Product not found</div>;

  /* ================= OPTIONS LABEL ================= */
  const optionLabel = (() => {
    if (category === 'watches') return 'Case size';
    if (category === 'phones' || category === 'tablets') return 'Capacity';
    return null;
  })();

  /* ================= CART / FAVORITES ================= */
  const configId = `${product.namespaceId}-${selectedColor}-${selectedCapacity}`;

  const isInCart = cart.some(item => item.configId === configId);
  const isFavorite = favorites.some(item => item.configId === configId);

  /* üî• –í–ê–ñ–ù–û: —Ç—É—Ç –º—ã —Ñ–∏–∫—Å–∏—Ä—É–µ–º —Ü–≤–µ—Ç –∏ –∫–∞—Ä—Ç–∏–Ω–∫—É */
  const preparedProduct = {
    ...product,
    configId,
    color: selectedColor,
    capacity: selectedCapacity,
    priceDiscount: computedPrice,
    images: computedImage ? [computedImage] : product.images,
  };

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {product.name}
      </h1>

      {/* ================= MAIN GRID ================= */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '420px 1fr',
          gap: 48,
          marginTop: 32,
        }}
      >
        {/* IMAGE */}
        <div
          style={{
            height: 420,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {computedImage && (
            <img
              src={`/${computedImage}`}
              alt={product.name}
              style={{
                maxWidth: '70%',
                maxHeight: '100%',
                filter: 'drop-shadow(0 0 40px rgba(255,255,255,0.85))',
              }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${computedPrice}
          </p>

          {/* COLORS */}
          {product.colorsAvailable && (
            <>
              <h3>Available colors</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.colorsAvailable.map(color => (
                  <button
                    key={color}
                    onClick={() => setSelectedColor(color)}
                    style={{
                      width: 22,
                      height: 22,
                      borderRadius: '50%',
                      border: 'none',
                      cursor: 'pointer',
                      background: color,
                      boxShadow:
                        color === selectedColor
                          ? '0 0 10px rgba(255,255,255,1)'
                          : '0 0 6px rgba(255,255,255,0.4)',
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {/* CAPACITY / CASE SIZE */}
          {optionLabel && product.capacityAvailable && (
            <>
              <h3 style={{ marginTop: 24 }}>{optionLabel}</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.capacityAvailable.map(option => (
                  <button
                    key={option}
                    className="neon-btn"
                    onClick={() => setSelectedCapacity(option)}
                    style={{
                      opacity: option === selectedCapacity ? 1 : 0.5,
                      textShadow:
                        option === selectedCapacity
                          ? '0 0 8px rgba(255,160,220,0.9)'
                          : undefined,
                    }}
                  >
                    {option}
                  </button>
                ))}
              </div>
            </>
          )}

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className={`neon-btn ${isInCart ? 'active' : ''}`}
              disabled={isInCart}
              onClick={() => addToCart(preparedProduct)}
            >
              {isInCart ? '‚úì Added' : 'Add to cart'}
            </button>

            <button
              className={`neon-btn ${isFavorite ? 'active' : ''}`}
              onClick={() => !isFavorite && addToFavorites(preparedProduct)}
            >
              {isFavorite ? '‚ô• Favorited' : '‚ô° Favorite'}
            </button>
          </div>
        </div>
      </div>

      {/* ================= DESCRIPTION ================= */}
      {product.description && (
        <div style={{ marginTop: 64, maxWidth: 960 }}>
          <h2 className="neon-text" style={{ marginBottom: 24 }}>
            Description
          </h2>

          {product.description.map(section => (
            <div key={section.title} style={{ marginBottom: 32 }}>
              <h3
                style={{
                  fontSize: 18,
                  marginBottom: 12,
                  textShadow: '0 0 8px rgba(255,160,220,0.6)',
                }}
              >
                {section.title}
              </h3>

              {section.text.map((paragraph, idx) => (
                <p
                  key={idx}
                  style={{
                    opacity: 0.75,
                    lineHeight: 1.6,
                    marginBottom: 8,
                  }}
                >
                  {paragraph}
                </p>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ProductDetails;
