import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState, useMemo } from 'react';
import {
  getPhones,
  getTablets,
  getAccessories,
} from '../api/productsApi';

function ProductDetails({ cart, favorites, addToCart, addToFavorites }) {
  const { category, productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedCapacity, setSelectedCapacity] = useState(null);

  /* ================= LOAD ================= */
  useEffect(() => {
    const loaders = {
      phones: getPhones,
      tablets: getTablets,
      accessories: getAccessories,
    };

    loaders[category]().then(items => {
      const found = items.find(p => p.id === productId);
      setProduct(found || null);

      if (found) {
        setSelectedColor(found.colorsAvailable?.[0] || found.color || null);
        setSelectedCapacity(found.capacityAvailable?.[0] || null);
      }

      setLoading(false);
    });
  }, [category, productId]);

  const hasCapacity = Boolean(product?.capacityAvailable?.length);

  /* ================= PRICE ================= */
  const price = useMemo(() => {
    if (!product) return null;
    if (!hasCapacity) return product.priceDiscount;

    const base = product.capacityAvailable.indexOf(product.capacity);
    const current = product.capacityAvailable.indexOf(selectedCapacity);

    return product.priceDiscount + (current - base) * 100;
  }, [product, selectedCapacity, hasCapacity]);

  /* ================= IMAGE ================= */
  const image = useMemo(() => {
    if (!product || !selectedColor || !product.images?.length) {
      return null;
    }

    const example = product.images[0];
    const root = example.split(`/${product.color}/`)[0];

    return `${root}/${selectedColor}/00.webp`;
  }, [product, selectedColor]);

  /* ================= TITLE ================= */
  const title = useMemo(() => {
    if (!product) return '';

    let name = product.name;

    if (product.color) {
      name = name.replace(product.color, selectedColor);
    }

    if (hasCapacity && product.capacity) {
      name = name.replace(product.capacity, selectedCapacity);
    }

    return name;
  }, [product, selectedColor, selectedCapacity, hasCapacity]);

  /* ================= CONFIG ================= */
  const configId = useMemo(() => {
    if (!product) return '';

    return hasCapacity
      ? `${product.namespaceId}-${selectedColor}-${selectedCapacity}`
      : `${product.namespaceId}-${selectedColor}`;
  }, [product, selectedColor, selectedCapacity, hasCapacity]);

  const isInCart = cart.some(item => item.configId === configId);
  const isFavorite = favorites.some(item => item.configId === configId);

  /* ================= PRODUCT FOR CART ================= */
  const preparedProduct = {
    ...product,
    color: selectedColor,              // üî• –í–ê–ñ–ù–û
    capacity: hasCapacity ? selectedCapacity : null,
    image,
    priceDiscount: price,
    configId,
  };

  if (loading) return <div className="page">Loading...</div>;
  if (!product) return <div className="page">Product not found</div>;

  return (
    <div className="page">
      <button className="neon-btn" onClick={() => navigate(-1)}>
        ‚Üê Back
      </button>

      <h1 className="neon-text" style={{ marginTop: 24 }}>
        {title}
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '340px 1fr',
          gap: 48,
          marginTop: 32,
          alignItems: 'start',
        }}
      >
        {/* IMAGE */}
        <div style={{ display: 'flex', justifyContent: 'center' }}>
          {image && (
            <img
              src={`/${image}`}
              alt={title}
              style={{
                maxWidth: 240,
                maxHeight: 420,
                objectFit: 'contain',

                /* üå´ –ú–Ø–ì–ö–ò–ô –ë–ï–õ–´–ô GLOW */
                filter: `
                  drop-shadow(0 0 12px rgba(255,255,255,0.55))
                  drop-shadow(0 0 28px rgba(255,255,255,0.25))
                  drop-shadow(0 0 48px rgba(255,255,255,0.15))
                `,
              }}
            />
          )}
        </div>

        {/* INFO */}
        <div>
          <p style={{ opacity: 0.6 }}>{product.category}</p>

          <p className="neon-text" style={{ fontSize: 24, margin: '16px 0' }}>
            ${price}
          </p>

          {/* COLORS */}
          {product.colorsAvailable && (
            <>
              <h3>Available colors</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.colorsAvailable.map(color => (
                  <button
                    key={color}
                    onClick={() => setSelectedColor(color)}
                    style={{
                      width: 22,
                      height: 22,
                      borderRadius: '50%',
                      border: 'none',
                      cursor: 'pointer',
                      background: color,
                      boxShadow:
                        color === selectedColor
                          ? '0 0 8px rgba(255,255,255,0.9)'
                          : '0 0 4px rgba(255,255,255,0.3)',
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {/* CAPACITY / SCREEN */}
          {hasCapacity ? (
            <>
              <h3 style={{ marginTop: 24 }}>Capacity</h3>
              <div style={{ display: 'flex', gap: 12 }}>
                {product.capacityAvailable.map(cap => (
                  <button
                    key={cap}
                    className="neon-btn"
                    onClick={() => setSelectedCapacity(cap)}
                    style={{ opacity: cap === selectedCapacity ? 1 : 0.5 }}
                  >
                    {cap}
                  </button>
                ))}
              </div>
            </>
          ) : product.screen ? (
            <>
              <h3 style={{ marginTop: 24 }}>Screen size</h3>
              <p style={{ opacity: 0.8 }}>{product.screen}</p>
            </>
          ) : null}

          {/* ACTIONS */}
          <div style={{ marginTop: 32, display: 'flex', gap: 24 }}>
            <button
              className={`neon-btn ${isInCart ? 'active' : ''}`}
              disabled={isInCart}
              onClick={() => addToCart(preparedProduct)}
            >
              {isInCart ? '‚úì Added' : 'Add to cart'}
            </button>

            <button
              className={`neon-btn ${isFavorite ? 'active' : ''}`}
              onClick={() => !isFavorite && addToFavorites(preparedProduct)}
            >
              {isFavorite ? '‚ô• Favorited' : '‚ô° Favorite'}
            </button>
          </div>
        </div>
      </div>

      {/* DESCRIPTION */}
      {product.description && (
        <div style={{ marginTop: 64, maxWidth: 960 }}>
          <h2 className="neon-text" style={{ marginBottom: 24 }}>
            Description
          </h2>

          {product.description.map(section => (
            <div key={section.title} style={{ marginBottom: 32 }}>
              <h3 style={{ marginBottom: 12 }}>{section.title}</h3>
              {section.text.map((p, i) => (
                <p key={i} style={{ opacity: 0.75, lineHeight: 1.6 }}>
                  {p}
                </p>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ProductDetails;
