import { Link } from 'react-router-dom';

function ProductCard({
  product,
  cart,
  favorites,
  addToCart,
  addToFavorites,
}) {
  const isInCart = cart.some(item => item.id === product.id);
  const isFavorite = favorites.some(item => item.id === product.id);

  return (
    <div style={styles.card}>
      {/* üîó –ö–õ–ò–ö –ü–û –ö–ê–†–¢–û–ß–ö–ï */}
      <Link
        to={`/${product.category}/${product.id}`}
        style={styles.link}
      >
        <div style={styles.imageBox}>
          <img
            src={product.images?.[0]}
            alt={product.name}
            style={styles.image}
          />
        </div>

        <h3 style={styles.title}>{product.name}</h3>
        <p style={styles.category}>{product.category}</p>
        <p style={styles.price}>${product.priceDiscount}</p>
      </Link>

      {/* ACTIONS */}
      <div style={styles.actions}>
        <button
          style={styles.textBtn}
          disabled={isInCart}
          onClick={() => addToCart(product)}
        >
          {isInCart ? 'Added' : 'Add to cart'}
        </button>

        <button
          style={{
            ...styles.textBtn,
            ...(isFavorite ? styles.favorite : {}),
          }}
          onClick={() => addToFavorites(product)}
        >
          ‚ô• Favorite
        </button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: '220px',
    padding: '16px',
    borderRadius: '12px',
    background: 'rgba(20,24,55,0.55)',
    boxShadow: '0 0 0 1px rgba(180,140,255,0.15)',
    display: 'flex',
    flexDirection: 'column',
  },
  link: {
    color: 'inherit',
    textDecoration: 'none',
  },
  imageBox: {
    height: '140px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: '12px',
  },
  image: {
    maxWidth: '100%',
    maxHeight: '100%',
    objectFit: 'contain',
  },
  title: {
    fontSize: '14px',
    margin: '4px 0',
  },
  category: {
    fontSize: '11px',
    opacity: 0.6,
  },
  price: {
    margin: '8px 0',
    fontWeight: 500,
  },
  actions: {
    display: 'flex',
    gap: '12px',
    marginTop: 'auto',
  },
  textBtn: {
    background: 'none',
    border: 'none',
    padding: 0,
    color: '#ffffff',
    cursor: 'pointer',
    fontSize: '12px',
    textShadow: '0 0 8px rgba(255,255,255,0.7)',
  },
  favorite: {
    color: '#ff9adf',
    textShadow: '0 0 10px rgba(255,154,223,0.9)',
  },
};

export default ProductCard;
