// For PNG files
declare module '*.png' {
  const value: any;
  export = value;
}

// For JPEG/JPG files
declare module '*.jpg' {
  const value: any;
  export = value;
}

// For GIF files
declare module '*.gif' {
  const value: any;
  export = value;
}

// For SVG files
declare module '*.svg' {
  const value: any;
  export = value;
}

// For WebP files
declare module '*.webp' {
  const value: any;
  export = value;
}

declare module '*.ts' {
  const value: any;
  export default value;
}

declare module '*.json' {
  const value: any;
  export default value;
}
